INSERT INTO `pokemon2` (`id`, `name`, `power`, `hp`, `stage`, `set`, `pic`, `status`, `notes`) VALUES (63, 'Squirtle', '\n[W] Bubble: 10 damage. Flip a coin. If heads, the Defending Pokémon is now Paralyzed.', 40, 0, 'Base', 'a href=https://pkmncards.com/wp-content/uploads/squirtle-base-set-bs-63.jpg>Image</a>', 'N', '0');
INSERT INTO `pokemon2` (`id`, `name`, `power`, `hp`, `stage`, `set`, `pic`, `status`, `notes`) VALUES (64, 'Starmie', '\n[W][W] Recover: Discard 1 [W] Energy card attached to Starmie in order to use this attack. Remove all damage counters from Starmie.', 60, 1, 'Base', '<a href=https://pkmncards.com/wp-content/uploads/starmie-base-set-bs-64.jpg>Image</a>', 'N', '0');
INSERT INTO `pokemon2` (`id`, `name`, `power`, `hp`, `stage`, `set`, `pic`, `status`, `notes`) VALUES (65, 'Staryu', '\n[W] Slap: 20 damage.', 40, 0, 'Base', '<a href=https://pkmncards.com/wp-content/uploads/staryu-base-set-bs-65.jpg>Image</a>', 'H', '0');
INSERT INTO `pokemon2` (`id`, `name`, `power`, `hp`, `stage`, `set`, `pic`, `status`, `notes`) VALUES (66, 'Tangela', '\n[G][C] Bind: 20 damage. Flip a coin. If heads, the Defending Pokémon is now Paralyzed.', 50, 0, 'Base', '<a href=https://pkmncards.com/wp-content/uploads/tangela-base-set-bs-66.jpg>Image</a>', 'N', '0');
INSERT INTO `pokemon2` (`id`, `name`, `power`, `hp`, `stage`, `set`, `pic`, `status`, `notes`) VALUES (67, 'Voltorb', '[C] Tackle: 10 damage.', 40, 0, 'Base', '<a href=https://pkmncards.com/wp-content/uploads/voltorb-base-set-bs-67.jpg>Image</a>', 'H', '0');
INSERT INTO `pokemon2` (`id`, `name`, `power`, `hp`, `stage`, `set`, `pic`, `status`, `notes`) VALUES (68, 'Vulpix', '\n[R][R] Confuse Ray: 10 damage. Flip a coin. If heads, the Defending Pokémon is now Confused.', 50, 0, 'Base', '<a href=https://pkmncards.com/wp-content/uploads/vulpix-base-set-bs-68.jpg>Image</a>', 'H', '1');
INSERT INTO `pokemon2` (`id`, `name`, `power`, `hp`, `stage`, `set`, `pic`, `status`, `notes`) VALUES (69, 'Weedle', '[G] Poison Sting: 10 damage. Flip a coin. If heads, the Defending Pokémon is now Poisoned.', 40, 0, 'Base', '<a href=https://pkmncards.com/wp-content/uploads/weedle-base-set-bs-69.jpg>Image</a>', 'H', '1');
INSERT INTO `pokemon2` (`id`, `name`, `power`, `hp`, `stage`, `set`, `pic`, `status`, `notes`) VALUES (70, 'Clefairy Doll', '\nPlay Clefairy Doll as if it were a Basic Pokémon. While in play, Clefairy Doll counts a a Pokémon (instead of a Trainer card). Clefairy Doll has no attacks, can’t retreat, and can’t be Asleep, Confused, Paralyzed, or Poisoned. If Clefairy Doll is Knocked Out, it doesn’t count as a Knocked Out Pokémon. At any time during your turn before your attack, you may discard Clefairy Doll.', 10, 0, 'Base', '<a href=https://pkmncards.com/wp-content/uploads/clefairy-doll-base-set-bs-70.jpg>Image</a>', 'N', '2');
INSERT INTO `pokemon2` (`id`, `name`, `power`, `hp`, `stage`, `set`, `pic`, `status`, `notes`) VALUES (71, 'Computer Search', '\nDiscard 2 of the other cards from you hand in order to search your deck for any card and put it into your hand. Shuffle your deck afterward.', 0, 0, 'Base', '<a href=https://pkmncards.com/wp-content/uploads/computer-search-base-set-bs-71.jpg>Image</a>', 'N', '2');
INSERT INTO `pokemon2` (`id`, `name`, `power`, `hp`, `stage`, `set`, `pic`, `status`, `notes`) VALUES (72, 'Devolution Spray', 'Choose 1 of your own Pokémon in play and a Stage of Evolution. Discard all Evolution cards of that Stage or higher attached to that Pokémon. That Pokémon is no longer Asleep, Confused, Paralyzed, Poisoned, or anything else that might be the result of an attack (just as if you had evolved it).', 0, 0, 'Base', '<a href=https://pkmncards.com/wp-content/uploads/devolution-spray-base-set-bs-72.jpg>Image</a>', 'N', '1');
INSERT INTO `pokemon2` (`id`, `name`, `power`, `hp`, `stage`, `set`, `pic`, `status`, `notes`) VALUES (73, 'Impostor Professor Oak', '\nYour opponent shuffles his or her hand into his or her deck, then draws 7 cards.', 0, 0, 'Base', '<a href=https://pkmncards.com/wp-content/uploads/impostor-professor-oak-base-set-bs-73.jpg>Image</a>', 'N', '2');
INSERT INTO `pokemon2` (`id`, `name`, `power`, `hp`, `stage`, `set`, `pic`, `status`, `notes`) VALUES (74, 'Item Finder', '\nDiscard 2 of the other cards from your hand in order to put a Trainer card from your discard pile into your hand.', 0, 0, 'Base', '<a href=https://pkmncards.com/wp-content/uploads/item-finder-base-set-bs-74.jpg>Image</a>', 'H', '1');
INSERT INTO `pokemon2` (`id`, `name`, `power`, `hp`, `stage`, `set`, `pic`, `status`, `notes`) VALUES (75, 'Lass', '\nYou and your opponent show each other your hands, then shuffle all the Trainer cards from your hands into your decks.', 0, 0, 'Base', '<a href=https://pkmncards.com/wp-content/uploads/lass-base-set-bs-75.jpg>Image</a>', 'H', '2');
INSERT INTO `pokemon2` (`id`, `name`, `power`, `hp`, `stage`, `set`, `pic`, `status`, `notes`) VALUES (76, 'Pokémon Breeder', '\nPut a Stage 2 Evolution card from your hand on the matching Basic Pokémon. You can only play this card when you would be allowed to evolve that Pokémon anyway.', 0, 0, 'Base', '<a href=https://pkmncards.com/wp-content/uploads/pokemon-breeder-base-set-bs-76.jpg>Image</a>', 'H', '1');
INSERT INTO `pokemon2` (`id`, `name`, `power`, `hp`, `stage`, `set`, `pic`, `status`, `notes`) VALUES (77, 'Pokémon Trader', '\nTrade 1 of the Basic Pokémon or Evolution cards in your hand for 1 of the Basic Pokémon or Evolution cards from your deck. Show both cards to your opponent. Shuffle your deck afterward.', 0, 0, 'Base', '<a href=https://pkmncards.com/wp-content/uploads/pokemon-trader-base-set-bs-77.jpg>Image</a>', 'H', '2');
INSERT INTO `pokemon2` (`id`, `name`, `power`, `hp`, `stage`, `set`, `pic`, `status`, `notes`) VALUES (78, 'Scoop Up', 'Choose 1 of your Pokémon in play and return its Basic Pokémon card to your hand. (Discard all cards attached to that card.)', 0, 0, 'Base', '<a href=https://pkmncards.com/wp-content/uploads/scoop-up-base-set-bs-78.jpg>Image</a>', 'H', '0');
INSERT INTO `pokemon2` (`id`, `name`, `power`, `hp`, `stage`, `set`, `pic`, `status`, `notes`) VALUES (79, 'Super Energy Removal', 'Discard 1 Energy card attached to 1 of your Pokémon in order to choose 1 of your opponent’s Pokémon and up to 2 Energy cards attached to it. Discard those Energy cards.', 0, 0, 'Base', '<a href=https://pkmncards.com/wp-content/uploads/super-energy-removal-base-set-bs-79.jpg>Image</a>', 'N', '1');
INSERT INTO `pokemon2` (`id`, `name`, `power`, `hp`, `stage`, `set`, `pic`, `status`, `notes`) VALUES (80, 'Defender', '\nAttach Defender to 1 of your Pokémon. At the end of your opponent’s next turn, discard Defender. Damage done to that Pokémon by attacks is reduced by 20 (after applying Weakness and Resistance).', 0, 0, 'Base', '<a href=https://pkmncards.com/wp-content/uploads/defender-base-set-bs-80.jpg>Image</a>', 'H', '2');
INSERT INTO `pokemon2` (`id`, `name`, `power`, `hp`, `stage`, `set`, `pic`, `status`, `notes`) VALUES (81, 'Energy Retrieval', '\nTrade 1 of the other cards in your hand for up to 2 basic Energy cards from your discard pile.', 0, 0, 'Base', '<a href=https://pkmncards.com/wp-content/uploads/energy-retrieval-base-set-bs-81.jpg>Image</a>', 'N', '1');
INSERT INTO `pokemon2` (`id`, `name`, `power`, `hp`, `stage`, `set`, `pic`, `status`, `notes`) VALUES (82, 'Full Heal', 'Your Active Pokémon is no longer Asleep, Confused, Paralyzed, or Poisoned.', 0, 0, 'Base', '<a href=https://pkmncards.com/wp-content/uploads/full-heal-base-set-bs-82.jpg>Image</a>', 'H', '2');
INSERT INTO `pokemon2` (`id`, `name`, `power`, `hp`, `stage`, `set`, `pic`, `status`, `notes`) VALUES (83, 'Maintenance', '\nShuffle 2 of the other cards from your hand into your deck in order to draw a card.', 0, 0, 'Base', '<a href=https://pkmncards.com/wp-content/uploads/maintenance-base-set-bs-83.jpg>Image</a>', 'N', '0');
INSERT INTO `pokemon2` (`id`, `name`, `power`, `hp`, `stage`, `set`, `pic`, `status`, `notes`) VALUES (84, 'PlusPower', '\nAttach PlusPower to your Active Pokémon. At the end of your turn, discard PlusPower. If this Pokémon’s attack does damage to the Defending Pokémon (after applying Weakness and Resistance), the attack does 10 more damage to the Defending Pokémon.', 0, 0, 'Base', '<a href=https://pkmncards.com/wp-content/uploads/pluspower-base-set-bs-84.jpg>Image</a>', 'H', '0');
INSERT INTO `pokemon2` (`id`, `name`, `power`, `hp`, `stage`, `set`, `pic`, `status`, `notes`) VALUES (85, 'Pokémon Center', '\nRemove all damage counters from all of your own Pokémon with damage counters on them, then discard all Energy cards attached to those Pokémon.', 0, 0, 'Base', '<a href=https://pkmncards.com/wp-content/uploads/pokemon-center-base-set-bs-85.jpg>Image</a>', 'N', '0');
INSERT INTO `pokemon2` (`id`, `name`, `power`, `hp`, `stage`, `set`, `pic`, `status`, `notes`) VALUES (86, 'Pokémon Flute', '\nChoose 1 Basic Pokémon card from your opponent’s discard pile and put it onto his or her Bench. (You can’t play Pokémon Flute if your opponent’s Bench is full.)', 0, 0, 'Base', '<a href=https://pkmncards.com/wp-content/uploads/pokemon-flute-base-set-bs-86.jpg>Image</a>', 'H', '2');
INSERT INTO `pokemon2` (`id`, `name`, `power`, `hp`, `stage`, `set`, `pic`, `status`, `notes`) VALUES (87, 'Pokédex', '\nLook at up to 5 cards from the top of your deck and rearrange them as you like.', 0, 0, 'Base', '<a href=https://pkmncards.com/wp-content/uploads/pokedex-base-set-bs-87.jpg>Image</a>', 'H', '0');
INSERT INTO `pokemon2` (`id`, `name`, `power`, `hp`, `stage`, `set`, `pic`, `status`, `notes`) VALUES (88, 'Professor Oak', 'Discard your hand, then draw 7 cards.', 0, 0, 'Base', '<a href=https://pkmncards.com/wp-content/uploads/professor-oak-base-set-bs-88.jpg>Image</a>', 'N', '0');
INSERT INTO `pokemon2` (`id`, `name`, `power`, `hp`, `stage`, `set`, `pic`, `status`, `notes`) VALUES (89, 'Revive', '\nPut 1 Basic Pokémon card from your discard pile onto your Bench. Put damage counters on that Pokémon equal to half its HP (rounded down to the nearest 10). (You can’t play Revive if your Bench is full.)', 0, 0, 'Base', '<a href=https://pkmncards.com/wp-content/uploads/revive-base-set-bs-89.jpg>Image</a>', 'N', '1');
INSERT INTO `pokemon2` (`id`, `name`, `power`, `hp`, `stage`, `set`, `pic`, `status`, `notes`) VALUES (90, 'Super Potion', '\nDiscard 1 Energy card attached to your own Pokémon in order to remove up to 4 damage counters from that Pokémon.', 0, 0, 'Base', '<a href=https://pkmncards.com/wp-content/uploads/super-potion-base-set-bs-90.jpg>Image</a>', 'H', '2');
INSERT INTO `pokemon2` (`id`, `name`, `power`, `hp`, `stage`, `set`, `pic`, `status`, `notes`) VALUES (91, 'Bill', '\nDraw 2 cards.', 0, 0, 'Base', '<a href=https://pkmncards.com/wp-content/uploads/bill-base-set-bs-91.jpg>Image</a>', 'N', '0');
INSERT INTO `pokemon2` (`id`, `name`, `power`, `hp`, `stage`, `set`, `pic`, `status`, `notes`) VALUES (92, 'Energy Removal', '\nChoose 1 Energy card attached to 1 of your opponent’s Pokémon and discard it.', 0, 0, 'Base', '<a href=https://pkmncards.com/wp-content/uploads/energy-removal-base-set-bs-92.jpg>Image</a>', 'H', '0');
INSERT INTO `pokemon2` (`id`, `name`, `power`, `hp`, `stage`, `set`, `pic`, `status`, `notes`) VALUES (93, 'Gust of Wind', 'Choose 1 of your opponent’s Benched Pokémon and switch it with his or her Active Pokémon.', 0, 0, 'Base', '<a href=https://pkmncards.com/wp-content/uploads/gust-of-wind-base-set-bs-93.jpg>Image</a>', 'H', '2');
INSERT INTO `pokemon2` (`id`, `name`, `power`, `hp`, `stage`, `set`, `pic`, `status`, `notes`) VALUES (94, 'Potion', '\nRemove up to 2 damage counters from 1 of your Pokémon.', 0, 0, 'Base', '<a href=https://pkmncards.com/wp-content/uploads/potion-base-set-bs-94.jpg>Image</a>', 'N', '1');
INSERT INTO `pokemon2` (`id`, `name`, `power`, `hp`, `stage`, `set`, `pic`, `status`, `notes`) VALUES (95, 'Switch', 'Switch 1 of your own Benched Pokémon with your Active Pokémon.', 0, 0, 'Base', '<a href=https://pkmncards.com/wp-content/uploads/switch-base-set-bs-95.jpg>Image</a>', 'H', '0');
